export interface Pokemon {
  id: string;
  name: string;
  nameEn: string;
  soundPath: string;
  imagePath: string;
  generation: 1 | 2 | 3 | 4 | 5;
}

export interface GenerationInfo {
  id: 1 | 2 | 3 | 4 | 5;
  name: string;
  nameEn: string;
  region: string;
  regionEn: string;
  games: string;
  gamesEn: string;
  color: string;
  bgColor: string;
  range: [number, number];
}

