export function getGenerationById(id: number): GenerationInfo | undefined {
  return GENERATIONS.find(g => g.id === id);
}

function normalizeGenerations(generations?: number | number[]): number[] {
  if (generations === undefined) return [];
  return Array.isArray(generations) ? generations : [generations];
}

export function getPokemonListByGenerations(generations: number[]): Pokemon[] {
  if (generations.length === 0) return POKEMON_LIST;
  return POKEMON_LIST.filter(p => generations.includes(p.generation));
}

export function getRandomPokemon(generations?: number | number[]): Pokemon {
  const pool = getPokemonListByGenerations(normalizeGenerations(generations));
  return pool[Math.floor(Math.random() * pool.length)];
}

export function getRandomPokemons(count: number, excludeId?: string, generations?: number | number[]): Pokemon[] {
  const pool = getPokemonListByGenerations(normalizeGenerations(generations)).filter(p => p.id !== excludeId);
  return shuffleArray(pool).slice(0, count);
}

export function getPokemonById(id: string): Pokemon | undefined {
  return POKEMON_LIST.find(p => p.id === id);
}

export function shuffleArray<T>(array: T[]): T[] {
  const shuffled = [...array];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
}
