function soundPath(id: string): string {
  const num = parseInt(id, 10);
  return num <= 386 ? `/sounds/${id}.wav` : `/sounds/${id}.mp3`;
}

function imagePath(id: string): string {
  return `/poke_pic/${id}.png`;
}

